const PRECIP_WINDOW_HOURS = 6;
const FREEZE_F = 32;
const MAX_SNOW_CM = 0.5;

const inputs = $input.all();

return inputs.map(({ json: j }) => {
  const propertyId = j.Property_ID;
  const propertyName = j.Property_Name;

  const lat = j.Latitude ?? j.latitude ?? j.lat;
  const lon = j.Longitude ?? j.longitude ?? j.lon;

  const currentTempF = j.current?.temperature_2m;

  const hourly = j.hourly || {};
  const times = hourly.time || [];
  const temps = hourly.temperature_2m || [];
  const precip = hourly.precipitation || [];
  const snowfall = hourly.snowfall || [];

  const nowUtc = new Date();

  let precipInWindow = false;
  let maxSnowInWindow = 0;
  let overnightLowF = null;

  for (let i = 0; i < times.length; i++) {
    const tUtc = new Date(times[i]);
    const hoursAgo = (nowUtc - tUtc) / (1000 * 60 * 60);

    if (hoursAgo >= 0 && hoursAgo <= PRECIP_WINDOW_HOURS) {
      const p = Number(precip[i] ?? 0);
      const s = Number(snowfall[i] ?? 0);

      if (p > 0 || s > 0) precipInWindow = true;
      if (s > maxSnowInWindow) maxSnowInWindow = s;
    }

    const tf = temps[i];
    if (typeof tf === "number") {
      overnightLowF = (overnightLowF === null) ? tf : Math.min(overnightLowF, tf);
    }
  }

  const freezeByCurrent = typeof currentTempF === "number" && currentTempF <= FREEZE_F;
  const freezeByOvernightLow = typeof overnightLowF === "number" && overnightLowF <= FREEZE_F;

  const freezeCondition = freezeByCurrent || freezeByOvernightLow;
  const iceOnlyCondition = maxSnowInWindow <= MAX_SNOW_CM;

  const iceRisk = precipInWindow && freezeCondition && iceOnlyCondition;

  return {
    json: {
      Property_ID: propertyId,
      Property_Name: propertyName,
      Latitude: lat,
      Longitude: lon,
      evaluation: {
        iceRisk,
        precipInWindow,
        freezeByCurrent,
        freezeByOvernightLow,
        iceOnlyCondition,
      },
      weather_summary: {
        current_temp_f: currentTempF,
        overnight_low_f: overnightLowF,
        precip_window_hours: PRECIP_WINDOW_HOURS,
        precip_in_window: precipInWindow,
        max_snow_in_window_cm: maxSnowInWindow,
        snowfall_threshold_cm: MAX_SNOW_CM,
      },
    },
  };
});
